mapper.update:
UPDATE student
SET name        = #{name},
    age         = #{age},
    sex         = #{sex},
    is_graduate = #{isGraduate},
    stu_id      = #{stuId},
    class       = #{className},
    teacher     = #{teacher}
WHERE id = #{id}

mapper.select:
SELECT s.id, s.name, s.age, s.sex, s.teacher, s.img_url, s.stu_id, s.create_time, s.is_graduate, s.class AS className, t.name AS teacherName FROM `student` s LEFT JOIN teacher t ON t.id = s.teacher
AND s.name = #{name}
AND s.class = #{className}
